import React, { useState } from 'react'

const Radio = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [textFieldValue, setTextFieldValue] = useState('');

  const handleRadioButtonChange = (event) => {
    setSelectedOption(event.target.value);
    setTextFieldValue(event.target.value); // Update text field value based on selected option
  };

  return (

<div className='container'>
<div>
      <input
        type="radio"
        id="option1"
        name="radioOption"
        value="option1"
        checked={selectedOption === 'option1'}
        onChange={handleRadioButtonChange}
      />
      <label for="option1">React.js</label>

      <input
        type="radio"
        id="option2"
        name="radioOption"
        value="option2"
        checked={selectedOption === 'option2'}
        onChange={handleRadioButtonChange}
      />
      <label for="option2">vuw</label>

      <input
        type="radio"
        id="optionNone"
        name="radioOption"
        value=""
        checked={selectedOption === null}
        onChange={handleRadioButtonChange}
      />
      <label for="optionNone">None</label>

      <input
        type="text"
        value={textFieldValue}
        onChange={(event) => setTextFieldValue(event.target.value)}
      />
    </div>
</div>
 
  )
}

export default Radio
